{"linux":"// Place your key bindings in this file to override the defaults
[
    {
        "key": "ctrl+delete",
        "command": "deleteAllRight"
    },
    {
        "key": "ctrl+shift+tab ctrl+shift+r",
        "command": "deleteWordRight",
        "when": "textInputFocus && !editorReadonly"
    },
    {
        "key": "ctrl+delete",
        "command": "-deleteWordRight",
        "when": "textInputFocus && !editorReadonly"
    },
    {
        "key": "ctrl+backspace",
        "command": "deleteAllLeft"
    },
    {
        "key": "ctrl+numpad_divide",
        "command": "deleteWordLeft",
        "when": "textInputFocus && !editorReadonly"
    },
    {
        "key": "ctrl+backspace",
        "command": "-deleteWordLeft",
        "when": "textInputFocus && !editorReadonly"
    },
    {
        "key": "ctrl+k",
        "command": "editor.action.deleteLines",
        "when": "textInputFocus && !editorReadonly"
    },
    {
        "key": "ctrl+shift+k",
        "command": "-editor.action.deleteLines",
        "when": "textInputFocus && !editorReadonly"
    },
    {
        "key": "ctrl+shift+a",
        "command": "vscode-harpoon.addEditor"
    },
    {
        "key": "ctrl+8",
        "command": "vscode-harpoon.gotoEditor8"
    },
    {
        "key": "ctrl+shift+l",
        "command": "vscode-harpoon.addGlobalEditor"
    },
    {
        "key": "ctrl+1",
        "command": "vscode-harpoon.gotoEditor1"
    },
    {
        "key": "ctrl+2",
        "command": "vscode-harpoon.gotoEditor2"
    },
    {
        "key": "ctrl+3",
        "command": "vscode-harpoon.gotoEditor3"
    },
    {
        "key": "ctrl+4",
        "command": "vscode-harpoon.gotoEditor4"
    },
    {
        "key": "ctrl+5",
        "command": "vscode-harpoon.gotoEditor5"
    },
    {
        "key": "ctrl+6",
        "command": "vscode-harpoon.gotoEditor6"
    },
    {
        "key": "ctrl+shift+1",
        "command": "vscode-harpoon.gotoGlobalEditor1"
    },
    {
        "key": "ctrl+shift+2",
        "command": "vscode-harpoon.gotoGlobalEditor2"
    },
    {
        "key": "ctrl+shift+3",
        "command": "vscode-harpoon.gotoGlobalEditor3"
    },
    {
        "key": "ctrl+shift+4",
        "command": "vscode-harpoon.gotoGlobalEditor4"
    },
    {
        "key": "ctrl+shift+5",
        "command": "vscode-harpoon.gotoGlobalEditor5"
},
{
        "key": "ctrl+shift+[BracketLeft]",
        "command": "workbench.action.terminal.new"
},
{
        "key": "ctrl+'",
        "command": "workbench.action.terminal.toggleTerminal",
        "when": "terminal.active"
},
{
        "key": "ctrl+shift+[BracketLeft]",
        "command": "-workbench.action.terminal.toggleTerminal",
        "when": "terminal.active"
},
{
        "key": "ctrl+;",
        "command": "editor.action.commentLine",
        "when": "editorTextFocus && !editorReadonly"
},
{
        "key": "ctrl+/",
        "command": "-editor.action.commentLine",
        "when": "editorTextFocus && !editorReadonly"
},
{
        "key": "ctrl+shift",
        "command": "editor.action.formatDocument",
        "when": "editorHasDocumentFormattingProvider && editorTextFocus && !editorReadonly && !inCompositeEditor"
},
{
        "key": "ctrl+shift+i",
        "command": "-editor.action.formatDocument",
        "when": "editorHasDocumentFormattingProvider && editorTextFocus && !editorReadonly && !inCompositeEditor"
}
]"}
